mapper.select:
SELECT u.*, GROUP_CONCAT(DISTINCT r.name) AS role_names, GROUP_CONCAT(DISTINCT p.post_name) AS post_names, d.name AS dept_name FROM sys_user u LEFT JOIN sys_user_role ur ON u.id = ur.user_id LEFT JOIN sys_role r ON ur.role_id = r.id AND r.deleted = 0 LEFT JOIN sys_user_post up ON u.id = up.user_id LEFT JOIN sys_post p ON up.post_id = p.id AND p.deleted = 0 LEFT JOIN sys_dept d ON u.dept_id = d.id AND d.deleted = 0 WHERE u.deleted = 0
AND u.username LIKE CONCAT('%', #{username}, '%')
AND u.phone LIKE CONCAT('%', #{phone}, '%')
AND u.status = #{status}
AND ur.role_id = #{roleId}
AND u.dept_id IN
#{deptId}
GROUP BY u.id ORDER BY u.create_time DESC
